export const breakpointSizes = {
    mobile: '720px',
    laptop: '1024px',
    desktop: '1280px',
};

export const mobile = `(min-width: ${breakpointSizes.mobile})`;

export const laptop = `(min-width: ${breakpointSizes.laptop})`;

export const desktop = `(min-width: ${breakpointSizes.desktop})`;
